sys_script_include.script:
var GetEmailAddress = Class.create();
// Extend the AbsttractAjaxProcessor 
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// define the function to be invoked from Client Script through sysparm_name argument
	getEmailspan: function(){
		var userRec=new GlideRecord('sys_user');
		//Passed on to SI through another sysparm_<userdefined> arg
		userRec.get(this.getParameter('sysparm_span'));
		return userRec.email+'';
	},

    type: 'GetEmailAddress'
});
